(function(d3, fc, sc) {
    'use strict';

    // TODO: Move to /model
    sc.menu.option = function(displayString, valueString, option, icon) {
        return {
            icon: icon || null,
            displayString: displayString, // TODO: is 'displayName' better?
            valueString: valueString, // TODO: is this an id?
            option: option, // TODO: Ideally, remove.
            isSelected: false
        };
    };

    /**
     * Generates a menu option similar to those generated by sc.menu.option from a sc.model.product object
     *
     * @param {product} product - The product used to generate a menu option
     */
    sc.menu.productAdaptor = function(product) {
        return {
            displayString: product.display,
            option: product
        };
    };

    /**
     * Generates a menu option similar to those generated by sc.menu.option from a sc.model.period object
     *
     * @param {period} period - The period used to generate a menu option
     */
    sc.menu.periodAdaptor = function(period) {
        return {
            displayString: period.display,
            option: period
        };
    };

})(d3, fc, sc);
